    <script>
        function makeTimer() {
            var today = new Date();
            var tomorrow = new Date(today)
            tomorrow.setDate(tomorrow.getDate() + 1);
            var tomorrow_splitted = tomorrow.toString().split(' ');
            console.log(tomorrow);
            var tomorrow_day = tomorrow_splitted[2];
            var tomorrow_month = tomorrow_splitted[1];
            var tomorrow_year = tomorrow_splitted[3];
            console.log(tomorrow_day);
            console.log(tomorrow_month);
            console.log(tomorrow_year);
            var endTime = new Date("" + tomorrow_day + " " + tomorrow_month + " " + tomorrow_year + " 0:00:00 GMT+01:00");
            endTime = Date.parse(endTime);
            endTime = endTime / 1000;
            var now = new Date();
            now = (Date.parse(now) / 1000);
            var timeLeft = endTime - now;
            console.log(timeLeft);
            var days = Math.floor(timeLeft / 86400);
            var hours = Math.floor(timeLeft / 3600);
            var hours_to_calc = Math.floor((timeLeft - (days * 86400)) / 3600);
            var minutes = Math.floor((timeLeft - (days * 86400) - (hours_to_calc * 3600)) / 60);
            var seconds = Math.floor((timeLeft - (days * 86400) - (hours_to_calc * 3600) - (minutes * 60)));
            var passDays = " < div > Vote ends In: " + days + "
            days: < /div>";
            var passHours = hours_to_calc + " < div > hours: < /div>";
            var passMinutes = minutes + " < div > minutes < /div>";
            if (hours_to_calc < "10") {
                hours_to_calc = "0" + hours_to_calc;
            }
            if (minutes < "10") {
                minutes = "0" + minutes;
            }
            if (seconds < "10") {
                seconds = "0" + seconds;
            }
            $(".Offer_hours").html(hours);
            $(".Offer_minutes").html(minutes);
            $(".Offer_seconds").html(seconds);
        }
        setInterval(function() {
            makeTimer();
        }, 1000);
    </script>
